INSERT INTO `books` (`id`, `name`, `slug`, `image`, `category_id`, `author`, `active`, `outstanding`, `description`, `view`, `created_at`, `updated_at`) VALUES
(1, 'Bán Hàng Thông Minh Qua Điện Thoại Và Internet', 'ban-hang-thong-minh-qua-dien-thoai-va-internet', 'nhasachmienphi-ban-hang-thong-minh-qua-dien-thoai-va-internet15.gif', 2, 'Josiane Chriqui Feigon', 1, 1, '<p>Bạn l&agrave; một nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng từ xa chuy&ecirc;n nghiệp: l&agrave; người ti&ecirc;n phong trong trận chiến của doanh nghiệp nhằm gi&agrave;nh ưu thế tr&ecirc;n thị trường, l&agrave; người cưỡi đầu ngọn s&oacute;ng biến đổi của thế giới Sales 2.0 v&agrave; đấu tranh thu h&uacute;t sự ch&uacute; &yacute; của những kh&aacute;ch h&agrave;ng tiềm năng tự do, bận rộn.. Bạn cần t&igrave;m kiếm những c&ocirc;ng ty hay c&aacute; nh&acirc;n c&oacute; năng lực, thu h&uacute;t họ qua điện thoại, Internet v&agrave; kết th&uacute;c hợp đồng &ndash; một c&aacute;ch nhanh ch&oacute;ng. Hệ thống 10 c&ocirc;ng cụ tiếp thị từ xa th&ocirc;ng minh mạnh mẽ, to&agrave;n diện cho b&aacute;n h&agrave;ng qua điện thoại v&agrave; Interner của Josiena Feigon sẽ gi&uacute;p bạn l&agrave;m tất cả những điều đ&oacute;, v&agrave; c&ograve;n hơn thế nữa.</p>', 1001, '2021-08-16 10:10:25', '2021-08-16 10:59:46'),
(2, 'Luật 101 – Mọi Điều Bạn Cần Biết Về Pháp Luật Hoa Kỳ', 'luat-101-–-moi-dieu-ban-can-biet-ve-phap-luat-hoa-ky', 'nhasachmienphi-luat-101-moi-dieu-ban-can-biet-ve-phap-luat-hoa-ky14.jpg', 9, 'Jay M. Feinman', 1, 1, '<p>Phi&ecirc;n bản đầu ti&ecirc;n v&agrave; t&aacute;i bản lần thứ hai của cuốn s&aacute;ch b&aacute;n chạy nhất,&nbsp;<em><strong>Luật 101 &ndash; Mọi điều bạn cần biết về ph&aacute;p luật Hoa Kỳ</strong></em>&nbsp;đ&atilde; cung cấp cho độc giả một bức tranh sinh động v&agrave; ch&acirc;n thực về hệ thống ph&aacute;p luật của Hoa Kỳ. V&agrave; trong lần t&aacute;i bản thứ ba n&agrave;y, Jay M. Feinman đ&atilde; tiếp tục đưa ra c&aacute;i nh&igrave;n tổng qu&aacute;t hơn v&agrave; cập nhật đầy đủ về hệ thống ph&aacute;p luật Hoa Kỳ, bao gồm tư liệu mới về c&aacute;c vụ kiện được T&ograve;a &aacute;n Tối cao Hoa Kỳ x&eacute;t xử năm 2009, kh&iacute;a cạnh ph&aacute;p l&yacute; về cuộc chiến chống khủng bố (kể cả những người bị giam giữ ở Guantanamo v&agrave; cổng từ an ninh), v&agrave; những tiến triển gần đ&acirc;y nhất của luật về Internet. Cuốn s&aacute;ch chứa đựng to&agrave;n bộ những điều hữu &iacute;ch tương đương với chương tr&igrave;nh năm học thứ nhất ở c&aacute;c trường đại học luật tr&ecirc;n thế giới, bước đầu đưa người đọc đến với lối tư duy của một luật sư thực sự với khối lượng kiến thức của một kh&oacute;a học bồi dưỡng cơ bản về nghề.</p>', 2102, '2021-08-16 14:59:26', '2021-08-16 15:00:27'),
(3, 'Công Nghệ Blockchain', 'cong-nghe-blockchain', 'nhasachmienphi-cong-nghe-blockchain46.jpg', 7, 'Nhiều tác giả', 1, 1, '<p>Blockchain l&agrave; chủ đề đang v&ocirc; c&ugrave;ng n&oacute;ng tr&ecirc;n to&agrave;n cầu hiện nay. N&oacute; c&ugrave;ng với&nbsp;Bitcoin&nbsp;v&agrave; tiền kỹ thuật số trở th&agrave;nh đề t&agrave;i b&agrave;n luận tr&ecirc;n rất nhiều mặt b&aacute;o v&agrave; trong những cuộc tr&ograve; chuyện của mọi người. Tuy nhi&ecirc;n, khi n&oacute;i về blockchain vẫn c&ograve;n nhiều tranh c&atilde;i. C&oacute; người lo lắng rằng Bitcoin c&oacute; thể chỉ l&agrave; bong b&oacute;ng, nhiều người cho rằng c&ocirc;ng nghệ ph&iacute;a sau n&oacute; l&agrave; một sự đột ph&aacute;, v&agrave; c&ocirc;ng nghệ ấy sẽ tiếp tục con đường của m&igrave;nh cho đến khi được chấp nhận v&agrave; t&iacute;ch hợp với Internet.</p>', 12094, '2021-08-16 15:01:10', '2021-08-16 08:01:10'),
(4, 'Tự Học Tiếng Anh Hiệu Quả', 'tu-hoc-tieng-anh-hieu-qua', 'nhasachmienphi-tu-hoc-tieng-anh-hieu-qua44.jpg', 8, 'Nguyễn Thị Hà Bắc', 1, 1, '<p>Nếu&nbsp; như bạn&nbsp; mong&nbsp; muốn&nbsp; giao&nbsp; tiếp&nbsp; tiếng&nbsp; Anh&nbsp; th&agrave;nh&nbsp; thạo,&nbsp; chuy&ecirc;n nghiệp từ 3 tới 6 th&aacute;ng th&igrave; cuốn s&aacute;ch n&agrave;y sẽ l&agrave;m bạn thất vọng. Thực tế cho thấy chưa c&oacute; ai mới bắt đầu học tiếng&nbsp; Anh c&oacute; thể giao tiếp được tr&ocirc;i chảy trong thời gian từ 3 &ndash; 6 th&aacute;ng. Đ&oacute; l&agrave; ảo tưởng. Nhưng nếu như bạn đang t&igrave;m kiếm l&agrave;m như thế n&agrave;o c&oacute; thể sử dụng tiếng Anh giao tiếp th&agrave;nh thạo, chuy&ecirc;n nghiệp trong 1 năm tới th&igrave; xin ch&uacute;c mừng&nbsp; bạn.&nbsp; T&ocirc;i tin rằng&nbsp; những&nbsp; b&iacute;&nbsp; mật&nbsp; được tiết&nbsp; lộ&nbsp; trong&nbsp; cuốn s&aacute;ch&nbsp;n&agrave;y sẽ l&agrave;m bạn thỏa m&atilde;n với điều đ&oacute;.&nbsp;</p>', 8731, '2021-08-16 15:02:35', '2021-08-16 08:02:35'),
(5, 'Cao Thủ “Đổi Đen Thay Trắng”', 'cao-thu-“doi-den-thay-trang”', 'nhasachmienphi-cao-thu-doi-den-thay-trang57.png', 6, 'Phong Lưu Thư Ngốc', 1, 1, '<p>Một hack cơ khi bị Chủ Thần nh&igrave;n tr&uacute;ng th&igrave; sẽ chỉ l&agrave;m nh&acirc;n vật phản diện suốt mấy ngh&igrave;n năm, kiếp n&agrave;o cũng sẽ t&igrave;m đến con đường chết, rơi v&agrave;o kết cục bi thảm.Rốt cuộc một ng&agrave;y kia cũng tho&aacute;t khỏi sự khống chế của hệ thống phản diện, hắn quyết định điều tra ngược lại để b&aacute;o th&ugrave;, nhằm thay đổi vận mệnh. D&ugrave; nội t&acirc;m c&oacute; rữa n&aacute;t như thế n&agrave;o đi chăng nữa th&igrave; ngo&agrave;i mặt, hắn vẫn phải lu&ocirc;n đứng tr&ecirc;n chuẩn mực đạo đức cao nhất.</p>', 6521, '2021-08-16 15:03:58', '2021-08-16 08:03:58'),
(6, 'Đắc nhân tâm', 'dac-nhan-tam', 'nhasachmienphi-dac-nhan-tam17.jpg', 10, 'Dale Carnegie', 1, 1, '<p><em><strong>Đắc nh&acirc;n t&acirc;m&nbsp;</strong>&ndash;&nbsp;<strong>How to win friends and Influence People&nbsp;</strong>&nbsp;của Dale Carnegie l&agrave; quyển s&aacute;ch nổi tiếng nhất, b&aacute;n chạy nhất v&agrave; c&oacute; tầm ảnh hưởng nhất của mọi thời đại. T&aacute;c phẩm đ&atilde; được chuyển ngữ sang hầu hết c&aacute;c thứ tiếng tr&ecirc;n thế giới v&agrave; c&oacute; mặt ở h&agrave;ng trăm quốc gia.&nbsp;</em></p>', 3421, '2021-08-16 15:05:08', '2021-08-16 08:05:08'),
(7, 'Bão Lửa U23 – Thường Châu Tuyết Trắng', 'bao-lua-u23-–-thuong-chau-tuyet-trang', 'nhasachmienphi-bao-lua-u23-thuong-chau-tuyet-trang27.jpg', 11, 'Nhiều Tác Giả', 1, 1, '<p>Như ch&uacute;ng ta đều biết, những th&agrave;nh quả m&agrave; Đội tuyển U23 Việt Nam đạt được tại VCK U23 ch&acirc;u &Aacute; 2018 đã mang lại những cảm xúc khó qu&ecirc;n trong l&ograve;ng mỗi người con đất Việt. Những cảm hứng tuyệt vời đ&oacute; th&acirc;̣t hi&ecirc;́m hoi và đáng quý, cần được lan tỏa, lưu giữ theo thời gian, để lu&ocirc;n&nbsp; nhắc nhở ch&uacute;ng ta rằng, người Việt gi&agrave;u tinh thần d&acirc;n tộc, tinh thần thượng v&otilde;, gi&agrave;u &yacute; ch&iacute;, nghị lực, đặc biệt l&agrave; trước những thử th&aacute;ch, nghịch cảnh. Thế hệ trẻ Việt Nam ho&agrave;n to&agrave;n c&oacute; thể chinh phục những đỉnh cao trong nhiều lĩnh vực kh&aacute;c như kinh tế, gi&aacute;o dục, khoa học kỹ thuật&hellip; như trong thể thao như U23 đ&atilde; l&agrave;m được.</p>', 7689, '2021-08-16 15:07:56', '2021-08-16 08:07:56'),
(8, 'Tuổi Thơ Dữ Dội', 'tuoi-tho-du-doi', 'nhasachmienphi-tuoi-tho-du-doi64.jpg', 12, 'Phùng Quán', 1, 1, '<p>Ph&ugrave;ng Qu&aacute;n (1932 &ndash; 1995), sinh ra tại Thừa Thi&ecirc;n &ndash; Huế, &Ocirc;ng&nbsp;l&agrave; một nh&agrave; văn, nh&agrave; thơ Việt Nam. &Ocirc;ng bắt đầu viết trong khoảng thời gian của cuộc chiến tranh Đ&ocirc;ng Dương.<br />\r\nNăm 1988, cuốn tiểu thuyết Tuổi thơ dữ dội của &ocirc;ng được xuất bản v&agrave; nhận giải thưởng văn học Thiếu nhi của Hội nh&agrave; văn Việt Nam v&agrave;o hai năm sau đ&oacute;.</p>', 5142, '2021-08-16 15:10:28', '2021-08-16 08:10:28'),
(9, 'Những Huyền Thoại', 'nhung-huyen-thoai', 'nhasachmienphi-nhung-huyen-thoai50.jpg', 14, 'Roland Barthes', 1, 0, '<p>Cuốn s&aacute;ch gồm hai phần. Phần đầu lấy ti&ecirc;u đề tr&ugrave;ng với nhan đề của s&aacute;ch l&agrave; &ldquo;Những huyền thoại&rdquo; tập hợp 53 b&agrave;i &ocirc;ng viết từ 1954 đến 1956 về c&aacute;c vấn đề thời sự h&agrave;ng ng&agrave;y hết sức đa dạng &ocirc;ng quan s&aacute;t v&agrave; nghiền ngẫm trong x&atilde; hội Ph&aacute;p; chất liệu của những b&agrave;i viết ấy c&oacute; thể l&agrave; một bộ phim, một b&agrave;i b&aacute;o, một tấm ảnh ở trang b&igrave;a tờ tạp ch&iacute;, một cuộc triển l&atilde;m&hellip; Phần thứ hai mang ti&ecirc;u đề &ldquo;Huyền thoại, ng&agrave;y nay&hellip;&rdquo; (Le mythe, aujourd&rsquo;hui&hellip;) c&oacute; thể xem như lời hậu bạt d&agrave;i kh&eacute;p lại cuốn s&aacute;ch.</p>', 2434, '2021-08-16 15:13:24', '2021-08-16 08:13:24'),
(10, 'Trừng Phạt (Punishment)', 'trung-phat-punishment', 'nhasachmienphi-trung-phat-punishment8.jpg', 15, 'Võ Anh Thơ', 1, 0, '<p>&ldquo;Đối với trẻ em, người lớn rất quan trọng.&rdquo;Ba nạn nh&acirc;n l&agrave; những nam sinh mười ba tuổi, ngoan ngo&atilde;n lẫn hiền l&agrave;nh.V&agrave; cuối c&ugrave;ng, một b&iacute; mật chấn động đằng sau tất cả mọi chuyện&hellip; C&ugrave;ng một t&igrave;nh y&ecirc;u bao dung ấm &aacute;p của 2 con người.&ldquo;Luật d&agrave;nh cho trẻ vị th&agrave;nh ni&ecirc;n&rdquo; đ&atilde; &ldquo;bảo vệ&rdquo; những kẻ thủ &aacute;c nhỏ tuổi. Vậy, c&ocirc;ng l&yacute; sẽ được thực thi như thế n&agrave;o?</p>', 1980, '2021-08-16 15:16:05', '2021-08-16 08:16:05'),
(11, 'Giải Thuật Và Lập Trình', 'giai-thuat-va-lap-trinh', 'nhasachmienphi-giai-thuat-va-lap-trinh36.jpg', 7, 'Lê Minh Hoàng', 1, 1, '<p>Nếu bạn l&agrave; người đam m&ecirc; tin học, nếu bạn l&agrave; người muốn kh&aacute;m ph&aacute; về lập tr&igrave;nh, hẳn bạn phải biết đến một cuốn s&aacute;ch tin học rất nổi tiếng ở Việt Nam trong nhiều năm trở lại đ&acirc;y. Từ những học sinh kh&ocirc;ng chuy&ecirc;n đến những th&agrave;nh vi&ecirc;n đội tuyển thi quốc tế tin học, c&oacute; lẽ kh&ocirc;ng một ai chưa từng học qua cuốn s&aacute;ch được bi&ecirc;n soạn bởi một thầy gi&aacute;o trẻ những đầy t&agrave;i năng của trường Đại học Sư phạm H&agrave; Nội, thầy L&ecirc; Minh Ho&agrave;ng.</p>', 180, '2021-08-17 07:26:32', '2021-08-17 00:26:32'),
(12, 'Truyện tranh Doremon', 'truyen-tranh-doremon', 'nhasachmienphi-truyen-tranh-doremon3.jpg', 3, 'Fujiko F. Fujio', 1, 1, '<p>Bộ truyện được xem l&agrave; huyền thoại của Nhật Bản. N&oacute;i về cuộc sống của một ch&uacute; nh&oacute;c t&ecirc;n Nobita, t&iacute;nh t&igrave;nh hậu đậu v&agrave; ch&uacute; m&egrave;o m&aacute;y Doremon đến từ thế kỉ 22 c&ugrave;ng những người bạn Xuka, Chaien, Xeko; để từ đ&oacute; g&acirc;y ra bao tiếng cười v&agrave; r&uacute;t ra những b&agrave;i học &yacute; nghĩa cho người xem !!</p>', 833, '2021-08-18 03:05:07', '2021-08-17 20:05:07'),
(13, 'One Piece – Đảo Hải Tặc', 'one-piece-dao-hai-tac', 'nhasachmienphi-one-piece-dao-hai-tac56.jpg', 3, 'Eiichiro Oda', 1, 1, '<p>T&ecirc;n kh&aacute;c: <strong>Vua Hải Tặc</strong></p>\r\n\r\n<p><strong>One Piece</strong> l&agrave; c&acirc;u truyện kể về Luffy v&agrave; c&aacute;c thuyền vi&ecirc;n của m&igrave;nh. Khi c&ograve;n nhỏ, Luffy ước mơ trở th&agrave;nh Vua Hải Tặc. Cuộc sống của cậu b&eacute; thay đổi khi cậu v&ocirc; t&igrave;nh c&oacute; được sức mạnh c&oacute; thể co d&atilde;n như cao su, nhưng đổi lại, cậu kh&ocirc;ng bao giờ c&oacute; thể bơi được nữa. Giờ đ&acirc;y, Luffy c&ugrave;ng những người bạn hải tặc của m&igrave;nh ra khơi t&igrave;m kiếm kho b&aacute;u One Piece, kho b&aacute;u vĩ đại nhất tr&ecirc;n thế giới.</p>', 970, '2021-08-18 03:18:52', '2021-08-17 20:18:52'),
(14, 'Đấm Phát Chết Luôn', 'dam-phat-chet-luon', 'nhasachmienphi-dam-phat-chet-luon58.jpg', 3, 'Nhiều tác giả', 1, 1, '<p>Manga thể loại si&ecirc;u anh h&ugrave;ng với đặc trưng phồng t&ocirc;m đấm ph&aacute;t chết lu&ocirc;n&hellip; Lol!!! Nh&acirc;n vật ch&iacute;nh trong Onepunch-man l&agrave; Saitama, một con người m&agrave; nh&igrave;n đ&acirc;u cũng thấy &ldquo;tầm thường&rdquo;, từ khu&ocirc;n mặt v&ocirc; hồn, c&aacute;i đầu trọc l&oacute;c, cho tới thể h&igrave;nh long tong.</p>', 4586, '2021-08-18 03:21:10', '2021-08-17 20:21:10'),
(15, 'Thám Tử Lừng Danh Conan bản đẹp Gosho Aoyama', 'tham-tu-lung-danh-conan-ban-dep-gosho-aoyama', 'Thám-tử-lừng-danh-conan-bản-đẹp-Gosho-Aoyoma88.jpg', 3, 'Gosho Aoyama', 1, 1, '<p><strong><em>Th&aacute;m Tử Lừng Danh Conan bản đẹp Gosho Aoyama</em> &ndash; <em>Th&aacute;m tử lừng danh Conan</em> (bản đẹp)</strong>&nbsp;hay&nbsp;<strong>Detective Conan</strong>, l&agrave; một bộ truyện tranh trinh th&aacute;m Nhật Bản của t&aacute;c giả Aoyama Gōshō.Mở đầu c&acirc;u truyện, cậu học sinh trung học 17 tuổi Shinichi Kudo bị biến th&agrave;nh cậu b&eacute; Conan Edogawa. Shinichi trong phần đầu của Th&aacute;m tử lừng danh Conan được mi&ecirc;u tả l&agrave; một th&aacute;m tử học đường xuất sắc. Trong một lần đi chơi c&ocirc;ng vi&ecirc;n với Ran Mori, cậu t&igrave;nh cờ chứng kiến vụ một &aacute;n giết người, Cậu đ&atilde; gi&uacute;p cảnh s&aacute;t l&agrave;m s&aacute;ng tỏ vụ &aacute;n. Tr&ecirc;n đường về nh&agrave;, cậu v&ocirc; t&igrave;nh ph&aacute;t hiện một vụ l&agrave;m ăn mờ &aacute;m của những người đ&agrave;n &ocirc;ng mặc đồ đen. Ch&uacute;ng ph&aacute;t hiện ra cậu v&agrave; đ&atilde; cho cậu uống một thứ thuốc độc chưa qua thử nghiệm l&agrave; Apotoxin-4869 (APTX 4869) với mục đ&iacute;ch thủ ti&ecirc;u cậu. Tuy nhi&ecirc;n chất độc đ&atilde; kh&ocirc;ng giết chết Kudo m&agrave; chỉ khiến cậu bị teo nhỏ.</p>', 13460, '2021-08-18 03:28:00', '2021-08-17 20:28:00'),
(17, 'Dragon Quest – Dấu Ấn Rồng Thiêng', 'dragon-quest-–-dau-an-rong-thieng', 'nhasachmienphi-dragon-quest-dau-an-rong-thieng42.jpg', 3, 'Sanjo Riku', 1, 1, '<p><strong>Dragon Quest &ndash; Dấu ấn rồng thi&ecirc;ng</strong>&nbsp;(<strong>Dragon Quest &ndash;&nbsp;</strong><strong>Dai no Daiboken</strong>) c&ugrave;ng với <strong><a href=\"https://sachvui.com/ebook/7-vien-ngoc-rong-dragon-ball-ban-dep-tron-bo-akira-toriyama.2556.html\">7 vi&ecirc;n ngọc rồng</a> </strong>l&agrave; 2 bộ truyện ghi dấu ấn s&acirc;u đậm với những thế hệ bạn đọc 8x, 9x. Chắc hẳn vẫn c&ograve;n rất nhiều người muốn t&igrave;m đọc lại bộ truyện n&agrave;y để nhớ lại những k&yacute; ức rất đẹp một thời. Nh&acirc;n vật ch&iacute;nh trong truyện l&agrave;&nbsp;<strong>Dai no Daiboken</strong>&nbsp;&ndash;&nbsp;<strong>Dũng sĩ rồng Dai</strong>, từ một cậu b&eacute; hồn nhi&ecirc;n đ&atilde; nhanh ch&oacute;ng trở th&agrave;nh Dũng sĩ diệt trừ ma quỷ, niềm hy vọng lớn nhất của thế giới lo&agrave;i người. S&aacute;t c&aacute;nh c&ugrave;ng cậu l&agrave; những học tr&ograve; của Evan: Pop, Marm, Leona, Huynken.</p>', 9876, '2021-08-18 03:35:25', '2021-08-17 20:35:25'),
(18, 'Kingdom – Vương Giả Thiên Hạ', 'kingdom-vuong-gia-thien-ha', 'nhasachmienphi-kingdom-vuong-gia-thien-ha8.jpg', 3, 'Hara Yasuhisa', 1, 0, '<p><em><strong>Vương giả thi&ecirc;n hạ</strong></em>&nbsp;hay&nbsp;<em><strong>Kingdom</strong></em>&nbsp;trong tiếng Anh l&agrave; một bộ&nbsp;truyện tranh Nhật Bản&nbsp;được s&aacute;ng t&aacute;c v&agrave; minh họa bởi&nbsp;Hara YasuhisaTrải qua h&agrave;ng triệu năm kể từ thời đại của c&aacute;c vị thần. Đ&acirc;y l&agrave; thời m&agrave; kh&aacute;t vọng của một người c&oacute; thể xoay chuyển cả thế giới. Thời đại của hơn 500 năm binh biến: thời Chiến Quốc. Kingdom &ndash; Vương Giả Thi&ecirc;n Hạ l&agrave; c&acirc;u chuyển kể về một thiếu ni&ecirc;n t&ecirc;n T&iacute;n c&ugrave;ng những thử th&aacute;ch cam go, những trận chiến đẫm m&aacute;u m&agrave; cậu cần vượt qua để c&oacute; thể trở th&agrave;nh một vị tướng qu&acirc;n vĩ đại. T&aacute;c phẩm đ&atilde; gi&agrave;nh được giải thưởng văn h&oacute;a Tezuka Osamu lần thứ 17 v&agrave;o năm 2013.</p>', 6435, '2021-08-18 03:37:16', '2021-08-17 20:37:16'),
(19, 'Truyện Tranh Tây Du', 'truyen-tranh-tay-du', 'nhasachmienphi-truyen-tranh-tay-du24.jpg', 3, 'Trịnh Kiện Hoà - Đặng Chí Huy', 1, 0, '<p>Tr&ecirc;n đường đi họ c&ograve;n c&oacute; được sự gi&uacute;p đỡ của Tiểu Bạch Long, Đ&ocirc;ng cung th&aacute;i tử Ngạo Tuyết. Cả 3 c&ugrave;ng bắt đầu cuộc h&agrave;nh tr&igrave;nh T&acirc;y phương, vừa phải tr&aacute;nh sự truy s&aacute;t của Thi&ecirc;n ch&uacute;ng, vừa phải đương đầu với biết bao y&ecirc;u ma đang chờ đ&oacute;n. Lại một t&aacute;c phẩm được liệt v&agrave;o h&agrave;ng si&ecirc;u phẩm của hai t&aacute;c giả Đặng Ch&iacute; Huy v&agrave; Trịnh Kiện H&ograve;a sau Phong Thần K&yacute;.T&acirc;y Du hứa hẹn sẽ đem đến cho độc giả những m&agrave;n chiến đấu kinh điển v&agrave; những triết l&yacute; m&agrave; t&aacute;c giả gửi gắm trong bộ truyện.</p>', 1364, '2021-08-18 03:39:48', '2021-08-17 20:39:48'),
(20, 'Cha giàu cha nghèo', 'cha-giau-cha-ngheo', 'nhasachmienphi-cha-giau-cha-ngheo41.jpg', 10, 'Robert Kiyosaki và Sharon Lechter', 1, 1, '<p>Bạn sẽ l&agrave;m g&igrave; khi được cả hai người cha truyền dạy cho m&igrave;nh về chủ đề tiền bạc v&agrave; sự lựa chọn c&aacute;ch sống trong đời. Một người l&agrave; bố ruột, c&ograve;n người kia l&agrave; bố người bạn th&acirc;n nhất. Một người c&oacute; nền học vấn rất cao trong khi người kia chỉ học tới trung học, nhưng cả hai đều th&agrave;nh c&ocirc;ng trong sự nghiệp v&agrave; c&oacute; ảnh hưởng đến người kh&aacute;c.</p>', 2164, '2021-08-18 03:41:47', '2021-08-17 20:41:47'),
(21, 'Mặc Kệ Thiên Hạ – Sống Như Người Nhật', 'mac-ke-thien-ha-song-nhu-nguoi-nhat', 'nhasachmienphi-mac-ke-thien-ha-song-nhu-nguoi-nhat42.jpg', 10, 'Mari Tamagawa', 1, 0, '<p><strong><em>Cuốn s&aacute;ch gối đầu giường cho những người hay lo lắng, sợ h&atilde;i v&agrave; lu&ocirc;n thấy m&igrave;nh k&eacute;m may mắn</em></strong></p>\r\n\r\n<p>D&agrave;nh cho những ai muốn được sống l&agrave; ch&iacute;nh m&igrave;nh, cuộc đời của m&igrave;nh, tuổi trẻ của m&igrave;nh.Đ&atilde; đến l&uacute;c bạn n&ecirc;n dừng t&igrave;m kiếm sự an ủi ở người kh&aacute;c, hoặc chờ đợi sự gi&uacute;p đỡ từ một ai đ&oacute;. Bởi an ủi hay gi&uacute;p đỡ về mặt cảm x&uacute;c đ&ocirc;i khi giống như con dao hai lưỡi. N&oacute; c&oacute; thể gi&uacute;p bạn chống đỡ lo &acirc;u hay muộn phiền nhất thời, nhưng lại đẩy bạn ch&igrave;m s&acirc;u hơn v&agrave;o những cảm x&uacute;c ti&ecirc;u cực đ&oacute;. Giống như một đứa trẻ khi vấp ng&atilde;, bạn mong đợi một sự xoa dịu từ người lớn, m&agrave; qu&ecirc;n mất rằng sự &ldquo;hỗ trợ&rdquo; ấy chỉ c&agrave;ng khiến bạn m&atilde;i chẳng thể n&agrave;o &ldquo;biết đi&rdquo;.</p>', 19761, '2021-08-18 04:01:29', '2021-08-17 21:01:29'),
(22, 'Khi Người Ta Tư Duy', 'khi-nguoi-ta-tu-duy', 'nhasachmienphi-khi-nguoi-ta-tu-duy98.jpg', 10, 'James Allen', 1, 0, '<p>Hơn một thế kỷ đ&atilde; tr&ocirc;i qua kể từ lần xuất bản đầu ti&ecirc;n v&agrave;o năm 1902, &ldquo;Khi người ta tư duy&rdquo; vẫn lu&ocirc;n l&agrave; một trong những kiệt t&aacute;c b&aacute;n chạy nhất v&agrave; li&ecirc;n tiếp nhận được sự t&aacute;n dương, ca ngợi từ độc giả. Với h&agrave;ng triệu bản được ấn h&agrave;nh, cuốn s&aacute;ch đ&atilde; truyền cảm hứng tới h&agrave;ng<br />\r\ntriệu người tr&ecirc;n to&agrave;n thế giới.&nbsp;</p>', 1631, '2021-08-18 04:04:33', '2021-08-17 21:04:33'),
(23, 'Bí Mật Hành Trình Tình Yêu', 'bi-mat-hanh-trinh-tinh-yeu', 'nhasachmienphi-bi-mat-hanh-trinh-tinh-yeu7.jpg', 10, 'David Niven', 1, 0, '<p><em>&ldquo;T&igrave;nh y&ecirc;u l&agrave; một t&igrave;nh cảm cao cả, n&oacute; l&agrave;m t&aacute;i sinh những t&acirc;m hồn lạc lối v&agrave; kiến tạo n&ecirc;n những gi&aacute; trị vĩ đại nhất của con người.&rdquo; &mdash; Makarenko</em></p>\r\n\r\n<p>&ldquo;<em>T&igrave;nh y&ecirc;u l&agrave; điều đặc biệt v&agrave; kỳ diệu nhất của con người.</em></p>\r\n\r\n<p><em>C&oacute; được t&igrave;nh y&ecirc;u đ&atilde; kh&oacute; &ndash; giữ được t&igrave;nh y&ecirc;u bền vững c&ograve;n kh&oacute; hơn&rdquo;.</em></p>', 3419, '2021-08-18 04:06:35', '2021-08-17 21:06:35'),
(24, 'Dám Bị Ghét', 'dam-bi-ghet', 'nhasachmienphi-dam-bi-ghet68.jpg', 10, 'Koga Fumitake - Kishimi Ichiro', 1, 0, '<p>Dưới h&igrave;nh thức một cuộc đối thoại giữa Ch&agrave;ng thanh ni&ecirc;n v&agrave; Triết gia, cuốn s&aacute;ch tr&igrave;nh b&agrave;y một c&aacute;ch sinh động v&agrave; hấp dẫn những n&eacute;t ch&iacute;nh trong tư tưởng của nh&agrave; t&acirc;m l&yacute; học Alfred Adler, người được mệnh danh l&agrave; một trong &ldquo;ba người khổng lồ của t&acirc;m l&yacute; học hiện đại&rdquo;, s&aacute;nh ngang với Freud v&agrave; Jung. Kh&aacute;c với Freud cho rằng qu&aacute; khứ v&agrave; ho&agrave;n cảnh l&agrave; động lực l&agrave;m n&ecirc;n con người ta của hiện tại, Adler chủ trương &ldquo;cuộc đời ta l&agrave; do ta lựa chọn&rdquo;, v&agrave; t&acirc;m l&yacute; học Adler được gọi l&agrave; &ldquo;t&acirc;m l&yacute; học của l&ograve;ng can đảm&rdquo;.</p>', 8799, '2021-08-18 04:09:25', '2021-08-17 21:09:25'),
(26, 'Kinh Nghiệm Thành Công Của Ông Chủ Nhỏ', 'kinh-nghiem-thanh-cong-cua-ong-chu-nho', 'nhasachmienphi-kinh-nghiem-thanh-cong-cua-ong-chu-nho89.jpg', 16, 'Lão Mạc', 1, 0, '<p>Kinh Nghiệm Th&agrave;nh C&ocirc;ng Của &Ocirc;ng Chủ Nhỏ&nbsp;l&agrave; một cuốn s&aacute;ch c&oacute; nội dung kh&aacute;c biệt với những cuốn s&aacute;ch kinh doanh th&ocirc;ng thường kh&aacute;c, c&oacute; thể sẽ gi&uacute;p &iacute;ch được cho những bạn trẻ đ&atilde; v&agrave; đang dấn th&acirc;n v&agrave;o lĩnh vực kinh doanh. Trong cuốn s&aacute;ch n&agrave;y, trước ti&ecirc;n, t&aacute;c giả n&ecirc;u ra t&igrave;nh huống, sau đ&oacute; đi s&acirc;u ph&acirc;n t&iacute;ch, đưa ra những luận điểm về những t&igrave;nh tiết quan trọng trong c&acirc;u chuyện v&agrave; đề cập tới những lĩnh vực c&oacute; thể ứng dụng luận điểm đ&oacute;. Những c&acirc;u chuyện m&agrave; t&aacute;c giả viết ra kh&ocirc;ng được ph&acirc;n loại theo phương thức quản l&iacute; kinh doanh m&agrave; dựa theo đặc điểm c&aacute;ch l&agrave;m của doanh nghiệp. V&agrave; đặc biệt, đ&oacute; kh&ocirc;ng phải l&agrave; những c&acirc;u chuyện quen thuộc từng được đề cập trong gi&aacute;o tr&igrave;nh thương mại, m&agrave; phần lớn l&agrave; những điều t&acirc;m đắc v&agrave; những trải nghiệm của ch&iacute;nh t&aacute;c giả.</p>', 7963, '2021-08-18 04:18:51', '2021-08-17 21:18:51'),
(27, 'Tuyển Tập Truyện Ngắn Vũ Trọng Phụng', 'tuyen-tap-truyen-ngan-vu-trong-phung', 'nhasachmienphi-tuyen-tap-truyen-ngan-vu-trong-phung14.jpg', 12, 'Vũ Trọng Phụng', 1, 0, '<p>Nhắc đến Vũ Trọng Phụng người ta nghĩ ngay tới một nh&agrave; văn t&agrave;i hoa nhưng bạc mệnh. C&oacute; năng khiếu nghệ thuật từ nhỏ, &ocirc;ng s&aacute;ng t&aacute;c từ rất sớm v&agrave; thử nghiệm m&igrave;nh tr&ecirc;n nhiều thể loại: viết b&aacute;o, viết kịch, s&aacute;ng t&aacute;c truyện ngắn, truyện vừa, tiểu thuyết, ph&ecirc; b&igrave;nh văn học&hellip; Nhưng c&oacute; thể n&oacute;i, tiểu thuyết v&agrave; ph&oacute;ng sự l&agrave; hai thể loại th&agrave;nh c&ocirc;ng nhất của nh&agrave; văn t&agrave;i hoa n&agrave;y. Vũ Trọng Phụng được b&aacute;o giới thời bấy giờ mệnh danh l&agrave; &ldquo;&ocirc;ng vua ph&oacute;ng sự đất Bắc&rdquo;, l&agrave; &ldquo;nh&agrave; tiểu thuyết tr&aacute;c việt&rdquo;.</p>', 1, '2021-08-18 04:25:37', '2021-08-28 07:52:49'),
(28, 'Số đỏ', 'so-do', '2135.jpg', 12, 'Vũ Trọng Phụng', 1, 0, '<p><em><strong>Số đỏ</strong></em>&nbsp;l&agrave; một tiểu thuyết&nbsp;văn học của nh&agrave; văn Vũ Trọng Phụng, đăng ở H&agrave; Nội b&aacute;o từ số 40 ng&agrave;y 7/10/1936 v&agrave; được in th&agrave;nh s&aacute;ch lần đầu v&agrave;o năm 1938. Nhiều nh&acirc;n vật v&agrave; c&acirc;u n&oacute;i trong t&aacute;c phẩm đ&atilde; đi v&agrave;o cuộc sống đời thường v&agrave; t&aacute;c phẩm đ&atilde; được dựng th&agrave;nh kịch phim. Nh&acirc;n vật ch&iacute;nh của&nbsp;<em>Số đỏ</em>&nbsp;l&agrave; Xu&acirc;n - biệt danh l&agrave; Xu&acirc;n T&oacute;c đỏ, từ chỗ l&agrave; một kẻ bị coi l&agrave; <em>hạ lưu</em>, bỗng nhảy l&ecirc;n tầng lớp&nbsp;<em>danh gi&aacute;</em>&nbsp;của x&atilde; hội nhờ tr&agrave;o lưu&nbsp;<em>&Acirc;u h&oacute;a</em>&nbsp;của giới tiểu tư sản H&agrave; Nội khi đ&oacute;. T&aacute;c phẩm&nbsp;<em>Số đỏ</em>, cũng như c&aacute;c t&aacute;c phẩm kh&aacute;c của Vũ Trọng Phụng đ&atilde; từng bị cấm lưu h&agrave;nh tại VNDCCH&nbsp;trước năm 1975 cũng như tại Việt Nam thống nhất từ năm 1975 cho đến năm 1986</p>', 1, '2021-08-18 04:29:25', '2021-08-29 14:23:01');
